package org.ynsif.operators;

public class BitwiseOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x=8, y=10;
System.out.println(x&y);
System.out.println(x|y);
System.out.println(x^y);
System.out.println(x<<y);
System.out.println(x>>y);
	}

}
